Q = queue.Queue()


keeplooping=True
def threadFunc(so):
    while keeplooping:
        ln = ""
        while True:
            b = so.read(1)
            if len(b) == 0:
                return
            elif b == b"\n":
                break
            else:
                ln += b.decode(errors="replace")
        print(ln)
        Q.put(ln)
   
def read(fp,delim=None):
    s=b""
    while True:
        c = fp.read(1)
        if c == None:
            return s
        if (delim == None and c.isspace()) or c == delim:
            return s
        s += c
        if len(s) > 100:
            print("Line too long")
            assert 0
        
def readppm(fp):
    assert b"P6" == read(fp)
    w = int(read(fp))
    h = int(read(fp))
    read(fp,b"\n")
    data = fp.read()
    return w,h,data
    
def main():
    now = int(time.time())
    
    scons = os.getenv("SCONS",os.path.abspath("scons.bat"))
    scons = "..\\scons\\scons.bat"
    P = subprocess.Popen( [scons],
        stdin=subprocess.PIPE,
        stdout=subprocess.PIPE,
        stderr=subprocess.STDOUT)
        
    si = P.stdin
    thr = threading.Thread(target=threadFunc, args=(P.stdout,))
    thr.start()

    start = time.time()
    
    while True:
        try:
            line = Q.get(block=True,timeout=TIMEOUT).strip()
        except queue.Empty:
            print("Timeout reached waiting for output!",file=sys.stderr)
            return
    
        now = time.time()
        if now - start > TIMEOUT:
            print("Timeout reached!",file=sys.stderr)
            return
            
        if line == "DONE":
            
            tf = tempfile.NamedTemporaryFile(delete=False)
            try:
                tf.close()
                print("Using temporary file",tf.name)
                si.write(b"~c\n")
                # si.write("screendump {}\n".format( tf.name ).encode() )
                si.write(b"quit\n")
                si.flush()
                P.wait(timeout=TIMEOUT)
                with open(tf.name,"rb") as fp:
                    w,h,actual = readppm(fp)
            finally:
                os.unlink(tf.name)
 

            if w != 800:
                print("Width mismatch; expected 800; got '{}'".format(w))
                return
